    public static function createTheme($data)
    {
        $name = $data['name'];
        $id = str_replace(" ", "_", strtolower($name));

        $data['id'] = $id;

        self::$themes[$id] = $data;
    }

    public static function all()
    {
        self::build();

        return self::$themes;
    }

    public static function getById($id)
    {
        self::build();

        return self::$themes[$id];
    }
